import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import Post from "@/components/Post"

import { waynedahlberg } from "@/authors";
import image01 from "./swiftui-basics-01.jpg";
export { getStaticProps } from "@/getStaticProps";
