import { waynedahlberg } from "@/authors";
import image1 from "./figma-variants.jpg";
export { getStaticProps } from "@/getStaticProps";
export const meta = {
  title: `Figma Variants`,
  description: `Designing extensible components in Figma is fun!`,
  date: "2020-11-08T12:00:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = (props) => <Post meta={meta} {...props} />
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <p>{`Our team at Chatbooks has been fully invested in Figma for several years now. We found it replaced a host of 3rd party solutions that never quite played well together.`}</p>
    <p>{`One of the great things about Figma is their ability to push the envelope, listen to our feedback, and implement reasoned, smart and functional solutions. Their latest feature, `}<em parentName="p">{`variants`}</em>{`, comes at a great time as we're finalizing our company's design system â€” Breakfast.`}</p>
    <p>{`Variants allow us to package visual `}<em parentName="p">{`state`}</em>{` within singular or nested components. These states can be described by a simple key-value relationship for each property. For example, `}<inlineCode parentName="p">{`type:`}</inlineCode>{`, `}<inlineCode parentName="p">{`size:`}</inlineCode>{`, `}<inlineCode parentName="p">{`color:`}</inlineCode>{` could all be properties of the state of a button.`}</p>
    <img src={image1} alt="iOS 14 Status Bar Figma Component" />
    <p>{`Here is an attempt at building the native iOS Status Bar with variant components. You can also check it out and duplicate it from the Figma Community `}<a parentName="p" {...{
        "href": "https://www.figma.com/community/file/904065746347450024/iOS-14-Status-Bar"
      }}>{`here`}</a>{`.`}</p>
    <p>{`You might have to zoom in a bit.`}</p>
    <iframe width="800" height="450" src="https://www.figma.com/embed?embed_host=share&url=https%3A%2F%2Fwww.figma.com%2Ffile%2FQQHcyX67RRbKHnshLOJRsL%2FiOS-14-Status-Bar-Community%3Fnode-id%3D1%253A21" allowfullscreen></iframe>

    </MDXLayout>;
}

