import image from './card.jpg'
import earlyDays from './early-days.jpg'
import astromech from './astromech.jpg'
export { getStaticProps } from "@/getStaticProps";
export const meta = {
  title: `Stories as a new media type`,
  description: `Stories have become ubiquitous, and no one platform can claim ownership on them.`,
  date: '2021-3-13T14:24:00.000Z',
  authors: [waynedahlberg],
  image,
  discussion: 'https://github.com/tailwindlabs/tailwindcss/discussions/2511'
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = (props) => <Post meta={meta} {...props} />
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <p>{`The year 2020 has really thrown a wrench into all our lifestyles. Here at home, and around the world, the LDS Church has cancelled Sunday services due to the global COVID-19 pandemic. In addition to the great resources the Church has provided for home-centered learning, I wanted a convenient way to keep up with my daily reading of `}<em parentName="p">{`The Book of Mormon`}</em>{`.`}</p>
    <p>{`The Church's official app `}<a parentName="p" {...{
        "href": "https://apps.apple.com/us/app/gospel-library/id598329798"
      }}>{`Gospel Library`}</a>{` is a massive resource. It has everything you need for gospel study and lesson preparation. In my opinion, its sheer size makes it feel a bit heavy for daily scripture reading. I want to open my phone and start reading where I left off the day before. No footnotes, cross-references, chapter headings. Nothing but scripture verses â€” one at a time on screen.`}</p>
    <img src={earlyDays} alt="Early days of Verses interface." />
    <blockquote>
      <p parentName="blockquote">{`I want to open my phone and start reading where I left off the day before.`}</p>
    </blockquote>
    <div className="my-8 relative pb-9/16">
  <iframe className="absolute inset-0 h-full w-full" src="https://www.youtube.com/embed/lMW6dqHUdSQ" frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>
