import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import { waynedahlberg } from "@/authors";
import image1 from "./figma-variants.jpg";
import image2 from "./variants.jpg";
export const meta = {
  title: `Figma Variants`,
  description: `Designing extensible components in Figma is fun!`,
  date: "2020-11-08T12:00:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`Our team at Chatbooks has been fully invested in Figma for several years now. We found it replaced a host of 3rd party solutions that never quite played well together.`}</p>
    <p>{`One of the great things about Figma is their ability to push the envelope, listen to our feedback, and implement reasoned, smart and functional solutions. Their latest feature, `}<em parentName="p">{`variants`}</em>{`, comes at a great time as we're finalizing our company's design system — Breakfast.`}</p>
    <img src={image2} alt="Status Bar variations" />
    <p>{`Variants allow us to package visual `}<em parentName="p">{`state`}</em>{` within singular or even nested components. These states can be described by a simple key-value relationship for each property. For example, `}<inlineCode parentName="p">{`type:`}</inlineCode>{`, `}<inlineCode parentName="p">{`size:`}</inlineCode>{`, `}<inlineCode parentName="p">{`color:`}</inlineCode>{` could all be properties of the state of a button.`}</p>
    <p>{`Once a valid group of similar and consistent component states are selected, you simple click `}<strong parentName="p">{`Create Variant`}</strong>{`. Once created, you can add and edit the properties that define your variant states.`}</p>
    <p>{`To test my knowledge of variants, I decided to make my first published component in the Figma Community; an iOS 14 Status Bar. The status bar contains much more variable information than expected. My component is limited to appearance (light and dark mode), battery charging, battery level, wifi signal, network signal, notch and isCameraRecording.`}</p>
    <p>{`You can check out the file in the Figma Community by clicking the image below.`}</p>
    <a href="https://www.figma.com/community/file/904065746347450024/iOS-14-Status-Bar">
  <img src={image1} alt="iOS 14 Status Bar Figma Component" />
    </a>
    <p>{`Here's a embedded view as well. You may need to zoom in a bit.`}</p>
    <iframe width="800" height="450" src="https://www.figma.com/embed?embed_host=share&url=https%3A%2F%2Fwww.figma.com%2Ffile%2FQQHcyX67RRbKHnshLOJRsL%2FiOS-14-Status-Bar-Community%3Fnode-id%3D1%253A21" allowfullscreen></iframe>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
