import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import Post from "@/components/Post"

import { waynedahlberg } from "@/authors";
import image1 from "./r2d2-7590.jpg";
import image2 from "./r2d2-7594.jpg";
import image3 from "./r2d2-7600.jpg";
import image4 from "./r2d2-7623.jpg";
import image5 from "./r2d2-7624.jpg";
import image6 from "./r2d2-7625.jpg";
import image7 from "./r2d2-7632.jpg";
import image8 from "./r2d2-7636.jpg";
import image9 from "./r2d2-7637.jpg";
export { getStaticProps } from "@/getStaticProps";
export const meta = {
  title: `Logic Display Assembly`,
  description: `All of the lights`,
  date: "2016-02-24T14:24:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = (props) => <Post meta={meta} {...props} />
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <p>{`Cutting the fiber optic cables was so tedious. It took a very long time, and the perfectionist in me made it all worse. But wow, what a reward. Placing the last piece was so liberating. You’ve got to follow the color coded pattern to get the optics to line up with the right LED components.`}</p>
    <p>{`Eventually these will be able to render crude scrolling text, so alignment is key. There is a test pattern you can enable by turning the delay pot all the way counter-clockwise and restarting. See the first image for what the pattern looks like.`}</p>
    <iframe title="vimeo-player" src="https://player.vimeo.com/video/156970563" width="640" height="360" frameborder="0" allowfullscreen></iframe>
    <p>{`Can’t wait to get these installed in the dome. Here's a few more photos:`}</p>
    <img src={image1} alt="logics 1" />
    <img src={image2} alt="logics 2" />
    <img src={image3} alt="logics 3" />
    <p>{`These parts are laser-cut. They have significant burring, and will need to be first on the to-do list. I’ll need to secure a set of sand paper grits, steel wool, and Brillo pads to clean up the edges all around. Luckily I have some `}<a parentName="p" {...{
        "href": "http://www.walmart.com/ip/Eagle-One-Nevr-Dull-5-oz./15125758"
      }}>{`NEVR-DULL polish`}</a>{` from when I cleaned up the pipes on my Triumph. This little droid is going to SHINE.`}</p>
    <p>{`Here’s a few photos:`}</p>
    <img src={image4} alt="logics 4" />
    <img src={image5} alt="logics 5" />
    <img src={image6} alt="logics 6" />
    <img src={image7} alt="logics 7" />
    <img src={image8} alt="logics 8" />
    <img src={image9} alt="logics 9" />

    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
