import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import { waynedahlberg } from "@/authors";
import hero from "./hero-swiftui-part1-apps.png";
import previewDevice from "./previewDevice.jpg";
import previewLayout from "./previewLayout.jpg";
export const meta = {
  title: `Intro to SwiftUI pt 1 - Apps`,
  description: `The basics of the SwiftUI layout framework.`,
  date: "2020-11-17T19:51:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <img src={hero} alt="Swiftui Basics hero image" />
    <h3>{`What is SwiftUI?`}</h3>
    <p>{`SwiftUI is Apple's new framework for describing views, interface controls and layout tools for software development. It provides for controlling user input, managing data flow, and updating of views displayed to the user. I'm personally very excited about this new approach to app development. It's fundamentally different from the imperative approach with UIKit.`}</p>
    </MDXLayout>;
}

