import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import { waynedahlberg } from "@/authors";
import image1 from "./sundace-jewelry-class-1.jpg";
import image2 from "./sundace-jewelry-class-2.jpg";
import image3 from "./sundace-jewelry-class-3.jpg";
import image4 from "./sundace-jewelry-class-4.jpg";
import image5 from "./sundace-jewelry-class-5.jpg";
export const meta = {
  title: `Sundance Offsite`,
  description: `Jewelery Making`,
  date: "2019-05-19T14:24:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`Today was a welcome diversion to staring at screens. The Chatbooks Product Team took a trip to Sundance Resort as part of an offsite activity. It's so nice to work with amazing people. It was a real treat to hang out, have fun, be creative outside of the daily routine.`}</p>
    <p>{`Between jewelry and pottery, I chose the former. My wife loves tourquoise, and I'm a sucker for learning new things involving metal, hammers and fire.`}</p>
    <p>{`We all decided to make rings. It was surprisingly straightforward. The general steps were:`}</p>
    <ol>
      <li parentName="ol">{`Pick your stone`}</li>
      <li parentName="ol">{`Measure and cut the side support material`}</li>
      <li parentName="ol">{`Measure and cut the base plate`}</li>
      <li parentName="ol">{`Weld the sides to the base plate`}</li>
      <li parentName="ol">{`Cut and file the rough edges off`}</li>
      <li parentName="ol">{`Measure and cut the ring portion`}</li>
      <li parentName="ol">{`Stamp any initials`}</li>
      <li parentName="ol">{`Weld the ring portion to the back of the base plate`}</li>
      <li parentName="ol">{`Place in the acid bath`}</li>
      <li parentName="ol">{`Place the stone in the ring cup created by the side material and base plate`}</li>
      <li parentName="ol">{`Shape the sides to enclose the stone (using a small hammer or the side of the table)`}</li>
      <li parentName="ol">{`Polish!`}</li>
    </ol>
    <img src={image1} alt="jewelry-class" />
    <img src={image2} alt="jewelry-class" />
    <img src={image3} alt="jewelry-class" />
    <img src={image4} alt="jewelry-class" />
    <img src={image5} alt="jewelry-class" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
