import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import Post from "@/components/Post"

import { waynedahlberg } from "@/authors";
import image from "./surrounds.jpg";
export { getStaticProps } from "@/getStaticProps";
export const meta = {
  title: `Aluminum Logic Surrounds`,
  description: `Very first part has arrived`,
  date: "2016-02-10T14:24:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = (props) => <Post meta={meta} {...props} />
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <p>{`Pretty pumped right now. The very first of many parts arrived today. I was lucky to get in on the 2nd batch of `}<a parentName="p" {...{
        "href": "http://astromech.net/forums/showthread.php?26696-BC-Approved-Aluminum-Logic-Surrounds-Set-100-(January-2016)-Closed-Manufacturing&p=356528#post356528"
      }}>{`Jose Medina’s Aluminum Logic Surrounds run`}</a>{`.`}</p>
    <p>{`These are high quality, precision parts machined in a CNC Mill. Now just waiting for JoyMonkey lights to fit them into. After many hours of research, I’m leaning toward an all aluminum R2. It really doesn’t get more expensive than that, unless you favor stainless steel. Lots of people are making their frames and legs out of particle board, but to me, a wooden droid doesn’t make sense. I’m not concerned about the perceived added weight, or frankly the cost. This project will last at least a year and a half, so throwing \\$ at every once in a while, I’m okay with – as long as there are no wooden parts on my Astromech. I may capitulate and go the 3D printed route for some of the facia, but this is a droid. It needs to be solid metal.`}</p>
    <img src="{image}" alt="Logic Surrounds" />

    </MDXLayout>;
}

