import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import Post from "@/components/Post"

import { waynedahlberg } from "@/authors";
import image1 from "./IMG_8552.jpg";
export { getStaticProps } from "@/getStaticProps";
export const meta = {
  title: `Mothers Polish`,
  description: `Shine on you crazy diamond`,
  date: "2016-02-26T14:24:00.000Z",
  authors: [waynedahlberg],
  discussion: "https://github.com/tailwindlabs/tailwindcss/discussions/2511"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = (props) => <Post meta={meta} {...props} />
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <p>{`I read yesterday a post by a metallurgist in the Astromech forum. He said the best way to polish billet is to avoid sanding and removing material, just use some Mothers Billet Metal Polish. You put a small amount on a terry cloth and just rub on the dome until a black residue appears.`}</p>
    <p>{`Wait about a minute and then wipe the residue clean with a fresh terry cloth. I was skeptical, and the billet polish is not cheap, but I’m a believer now. Below is a photo of the dome after polishing the left side for less than 2 minutes. I used about a quarter size dab of polish.`}</p>
    <p>{`This weekend I finished both the inner and outer dome surfacing. Using 400, 600, 1000, 1500, then 2000 grit wet sand papers, followed by two rounds of Mothers Billet Polish. Also I marked all the panels according to the specs from `}<a parentName="p" {...{
        "href": "http://astromech.net"
      }}>{`Astromech.net`}</a>{`.`}</p>
    <img src={image1} alt="polished domes" />

    </MDXLayout>;
}

;
